def count_paths(m, n):
    # Initialize the dp table
    dp = [[0] * (n + 1) for _ in range(m + 1)]
    dp[0][0] = 1

    # Fill the dp table
    for i in range(m + 1):
        for j in range(n + 1):
            if i > 0:
                dp[i][j] += dp[i-1][j]
            if i > 1:
                dp[i][j] += dp[i-2][j]
            if j > 0:
