def f(n, memo = {}):
    if n in memo:
        return memo[n]
    if n == 1:
        return 1
    elif n % 2 == 0:
        result = 1 + f(n // 2, memo)
    else:
        result = 1 + f(3 * n + 1, memo)
    memo[n] = result
    return result

