from itertools import combinations

def find_minimum_spanning_trees():
    # Define the edges of the graph
    edges = {
        (1, 2): 2,
        (1, 4): 2,
        (2, 5): 2,
        (2, 3): 1,
        (3, 6): 2,
        (4, 5): 2,
        (5, 6): 1
    }

    # Sort the edges by weight
    sorted_edges = sorted(edges.items(), key=lambda x: x[1])

    # Initialize the minimum spanning trees count
    min_spanning_trees_count = 0

    # Function to check if a graph is connected
    def is_connected(graph):
        visited = set()
        stack = [1]
        while stack:
            node = stack.pop()
            if node not in visited:
                visited.add(node)
                for edge in graph:
                    if edge[0] == node and edge[1] not in visited:
                        stack.append(edge[1])
                    elif edge[1] == node and edge[0] not in visited:
                        stack.append(edge[0])
        return len(visited) == 6

    # Function to check if a graph has cycles
    def has_cycle(graph):
        visited = set()
        stack = [1]
        while stack:
            node = stack.pop()
            if node not in visited:
                visited.add(node)
                for edge in graph:
                    if edge[0] == node and edge[1] in visited:
                        return True
                    elif edge[1] == node and edge[0] in visited:
                        return True
                    elif edge[0] == node and edge[1] not in visited:
                        stack.append(edge[1])
                    elif edge[1] == node and edge[0] not in visited:
                        stack.append(edge[0])
        return False

    # Generate all possible combinations of edges
    for r in range(1, len(sorted_edges) + 1):
        for combination in combinations(sorted_edges, r):
            graph = [edge[0] for edge in combination]
            if len(graph) == 5 and is_connected(graph) and not has_cycle(graph):
                min_spanning_trees_count += 1

    return min_spanning_trees_count

