import math

def count_divisors(n):
    count = 0
    for i in range(1, n+1):
        if n % i == 0:
            count += 1
    return count

def total_secure_connections(N):
    # IDs range from 2 to N+1
    ids = range(2, N+2)
    # Dictionary to map number of divisors to list of IDs
    divisor_groups = {}
    for id in ids:
        divisors = count_divisors(id)
        if divisors in divisor_groups:
            divisor_groups[divisors].append(id)
        else:
            divisor_groups[divisors] = [id]
    # Calculate total connections
    total = 0
    for group in divisor_groups.values():
        n = len(group)
        if n >= 2:
            total += math.comb(n, 2)
    return total

