def calculate_probability(x, y, prob_right, prob_left, prob_up, prob_down, memo):
    if (x, y) in memo:
        return memo[(x, y)]
    if x == 3 and y == 4:
        return 1
    if x > 3 or y > 4:
        return 0
    prob = 0
    if x < 3:
        prob += prob_right * calculate_probability(x + 1, y, prob_right - 0.01, prob_left + 0.01, prob_up, prob_down, memo)
    if x > 0:
        prob += prob_left * calculate_probability(x - 1, y, prob_right + 0.01, prob_left - 0.01, prob_up, prob_down, memo)
    if y < 4:
        prob += prob_up * calculate_probability(x, y + 1, prob_right, prob_left, prob_up - 0.01, prob_down + 0.01, memo)
    if y > 0:
        prob += prob_down * calculate_probability(x, y - 1, prob_right, prob_left, prob_up + 0.01, prob_down - 0.01, memo)
    memo[(x, y)] = prob
    return prob

def calculate_total_probability():
