    dp = {}
    dp[1] = {'a': {1: 1}, 'b': {1: 1}}

    # Fill the DP table for lengths from 2 to 10
    for length in range(2, 11):
        dp[length] = {'a': {}, 'b': {}}
        for last_char in ['a', 'b']:
            for consecutive in dp[length-1][last_char]:
                count = dp[length-1][last_char][consecutive]
                # If we can add another 'last_char'
                if consecutive < 2:
                    if consecutive + 1 in dp[length][last_char]:
                        dp[length][last_char][consecutive + 1] += count
                    else:
                        dp[length][last_char][consecutive + 1] = count
                # Add the other character
                other_char = 'a' if last_char == 'b' else 'b'
                if 1 in dp[length][other_char]:
                    dp[length][other_char][1] += count
                else:
                    dp[length][other_char][1] = count

    # Sum all valid states for length 10
    total = 0
