def simulate_game(n):
    p = 1
    x_turn = True
    while p < n:
        if x_turn:
            # X's turn, try to find a move that will make p >= n
            for i in range(2, 10):
                if p * i >= n:
                    return True
            # If no such move is found, make the smallest possible move
            p *= 2
        else:
            # Y's turn, try to find a move that will make p >= n
            for i in range(2, 10):
                if p * i >= n:
                    return False
            # If no such move is found, make the smallest possible move
            p *= 2
        x_turn = not x_turn
    return x_turn

