def order(a, m):
    if gcd(a, m) != 1:
        return -1
    phi = 1
    temp = m
    i = 2
    while i * i <= temp:
        if temp % i == 0:
            count = 0
            while temp % i == 0:
                temp //= i
                count += 1
            phi *= (i - 1) * (i ** (count - 1))
        i += 1
    if temp > 1:
        phi *= (temp - 1)

    factors = []
    d = phi
    i = 1
    while i * i <= d:
        if d % i == 0:
            factors.append(i)
            if i * i != d:
                factors.append(d // i)
        i += 1
    factors.sort()

    for factor in factors:
        if pow(a, factor, m) == 1:
            return factor
    return -1

