from collections import deque

def bfs_water_measuring(bottles, target, max_moves=6, max_water=22):
    initial_state = (0, 0, 0, 0)  # Initial state with all bottles empty
    queue = deque([(initial_state, 0, 0)])  # (state, moves, total_water)
    visited = set()
    visited.add(initial_state)

    while queue:
        current_state, moves, total_water = queue.popleft()

        if moves > max_moves or total_water > max_water:
            continue

        for i in range(4):
            if current_state[i] == target:
                return True, moves, total_water, sum(x > 0 for x in current_state)

        for i in range(4):
            if current_state[i] < bottles[i]:
                new_state = list(current_state)
                new_state[i] = bottles[i]
                new_state = tuple(new_state)
                new_total_water = total_water + (bottles[i] - current_state[i])
                if new_state not in visited:
                    visited.add(new_state)
                    queue.append((new_state, moves + 1, new_total_water))

        for i in range(4):
            for j in range(4):
                if i != j and current_state[i] > 0 and current_state[j] < bottles[j]:
                    pour = min(current_state[i], bottles[j] - current_state[j])
                    new_state = list(current_state)
                    new_state[i] -= pour
                    new_state[j] += pour
                    new_state = tuple(new_state)
                    if new_state not in visited:
                        visited.add(new_state)
                        queue.append((new_state, moves + 1, total_water))

    return False, None, None, None

