def max_wall_area(poster_widths, wall_length):
    n = len(poster_widths)
    dp = [[0] * (wall_length + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(wall_length + 1):
            if poster_widths[i - 1] <= j:
                dp[i][j] = max(dp[i - 1][j], dp[i - 1][j - poster_widths[i - 1]] + poster_widths[i - 1])
            else:
                dp[i][j] = dp[i - 1][j]

