            result = (result - (possible_intervals[i] * possible_intervals[j] - overlap) % MOD) % MOD

    # Apply the inclusion-exclusion principle for three employees
    for i in range(len(intervals)):
        for j in range(i + 1, len(intervals)):
            for k in range(j + 1, len(intervals)):
                overlap = max(0, min(intervals[i], intervals[j], intervals[k]) - max(0, intervals[i] + intervals[j] + intervals[k] - 2 * total_minutes))
                result = (result + (possible_intervals[i] * possible_intervals[j] * possible_intervals[k] - overlap) % MOD) % MOD

    # Apply the inclusion-exclusion principle for four employees
    for i in range(len(intervals)):
        for j in range(i + 1, len(intervals)):
            for k in range(j + 1, len(intervals)):
                for l in range(k + 1, len(intervals)):
                    overlap = max(0, min(intervals[i], intervals[j], intervals[k], intervals[l]) - max(0, intervals[i] + intervals[j] + intervals[k] + intervals[l] - 3 * total_minutes))
                    result = (result - (possible_intervals[i] * possible_intervals[j] * possible_intervals[k] * possible_intervals[l] - overlap) % MOD) % MOD

    # Apply the inclusion-exclusion principle for five employees
    for i in range(len(intervals)):
        for j in range(i + 1, len(intervals)):
            for k in range(j + 1, len(intervals)):
                for l in range(k + 1, len(intervals)):
                    for m in range(l + 1, len(intervals)):
                        overlap = max(0, min(intervals[i], intervals[j], intervals[k], intervals[l], intervals[m]) - max(0, intervals[i] + intervals[j] + intervals[k] + intervals[l] + intervals[m] - 4 * total_minutes))
                        result = (result + (possible_intervals[i] * possible_intervals[j] * possible_intervals[k] * possible_intervals[l] * possible_intervals[m] - overlap) % MOD) % MOD

    # Apply the inclusion-exclusion principle for six employees
    for i in range(len(intervals)):
        for j in range(i + 1, len(intervals)):
            for k in range(j + 1, len(intervals)):
                for l in range(k + 1, len(intervals)):
                    for m in range(l + 1, len(intervals)):
                        for n in range(m + 1, len(intervals)):
                            overlap = max(0, min(intervals[i], intervals[j], intervals[k], intervals[l], intervals[m], intervals[n]) - max(0, intervals[i] + intervals[j] + intervals[k] + intervals[l] + intervals[m] + intervals[n] - 5 * total_minutes))
                            result = (result - (possible_intervals[i] * possible_intervals[j] * possible_intervals[k] * possible_intervals[l] * possible_intervals[m] * possible_intervals[n] - overlap) % MOD) % MOD

    # Apply the inclusion-exclusion principle for seven employees
    overlap = max(0, min(intervals) - max(0, sum(intervals) - 6 * total_minutes))
    result = (result + (possible_intervals[0] * possible_intervals[1] * possible_intervals[2] * possible_intervals[3] * possible_intervals[4] * possible_intervals[5] * possible_intervals[6] - overlap) % MOD) % MOD

    return result

