import itertools

def find_msts():
    # Define the edges and their weights
    edges = {
        (1, 2): 2,
        (1, 4): 2,
        (2, 3): 1,
        (2, 5): 2,
        (3, 6): 2,
        (4, 5): 2,
        (5, 6): 1
    }

    # Sort the edges by weight
    sorted_edges = sorted(edges.items(), key=lambda x: x[1])

    # Initialize the set of MSTs
    msts = set()

    # Function to check if a set of edges forms a connected graph
    def is_connected(edges):
        parent = {}
        rank = {}

        def find(node):
            if node not in parent:
                parent[node] = node
                rank[node] = 0
            if parent[node] != node:
                parent[node] = find(parent[node])
            return parent[node]

        def union(node1, node2):
            root1 = find(node1)
            root2 = find(node2)
            if root1 != root2:
                if rank[root1] > rank[root2]:
                    parent[root2] = root1
                else:
                    parent[root1] = root2
                    if rank[root1] == rank[root2]:
                        rank[root2] += 1

        for edge in edges:
            union(edge[0], edge[1])

        roots = set()
        for node in parent:
            roots.add(find(node))

        return len(roots) == 1

    # Function to check if a set of edges has no cycles
    def has_no_cycles(edges):
        parent = {}
        rank = {}

        def find(node):
            if node not in parent:
                parent[node] = node
                rank[node] = 0
            if parent[node] != node:
                parent[node] = find(parent[node])
            return parent[node]

        def union(node1, node2):
            root1 = find(node1)
            root2 = find(node2)
            if root1 != root2:
                if rank[root1] > rank[root2]:
                    parent[root2] = root1
                else:
                    parent[root1] = root2
                    if rank[root1] == rank[root2]:
                        rank[root2] += 1
            else:
                return False
            return True

        for edge in edges:
            if not union(edge[0], edge[1]):
                return False

        return True

    # Generate all possible combinations of edges
    for r in range(1, len(sorted_edges) + 1):
        for combination in itertools.combinations(sorted_edges, r):
            edges_in_combination = [edge[0] for edge in combination]
            if is_connected(edges_in_combination) and has_no_cycles(edges_in_combination):
                msts.add(tuple(sorted(edges_in_combination)))

    return len(msts)

print(find_msts())
