import requests

def count_empty_directories(url):
    response = requests.get(url)
    lines = response.text.splitlines()
    stack = []
    empty_count = 0

    for line in lines:
        if line.startswith('|-- '):
            stack.append(line[4:])
        elif line.startswith('-- '):
            stack.append(line[4:])
        elif line.startswith('    '):
            continue
        else:
            if stack and stack[-1] == line:
                stack.pop()
                if not any(l.startswith('|-- ') or l.startswith('-- ') for l in lines[lines.index(line)+1:]):
                    empty_count += 1
            else:
                continue

    return empty_count

