def count_paths(m, n):
    dp = [[0] * (n + 1) for _ in range(m + 1)]
    dp[0][0] = 1

    for i in range(m + 1):
        for j in range(n + 1):
            if i >= 1:
                dp[i][j] += dp[i - 1][j]
            if i >= 2:
                dp[i][j] += dp[i - 2][j]
            if j >= 1:
                dp[i][j] += dp[i][j - 1]
            if j >= 2:
                dp[i][j] += dp[i][j - 2]

    return dp[m][n]

