def count_paths(numpad, rows, cols, steps):
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    dp = [[[0] * cols for _ in range(rows)] for _ in range(steps)]

    # Initialize the first step
    for i in range(rows):
        for j in range(cols):
            if numpad[i][j] != -1:
                dp[0][i][j] = 1

    # Fill the dp array
    for k in range(1, steps):
        for i in range(rows):
            for j in range(cols):
                if numpad[i][j] == -1:
                    continue
                for di, dj in directions:
                    ni, nj = i + di, j + dj
                    if 0 <= ni < rows and 0 <= nj < cols and numpad[ni][nj] != -1:
                        dp[k][i][j] += dp[k-1][ni][nj]

    # Sum up the number of ways to reach each key in the last step
    total_paths = 0
    for i in range(rows):
        for j in range(cols):
            if numpad[i][j] != -1:
                total_paths += dp[steps-1][i][j]

    return total_paths

