def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def mod_pow(base, exp, mod):
    result = 1
    base %= mod
    while exp > 0:
        if exp % 2 == 1:
            result = (result * base) % mod
        base = (base * base) % mod
        exp //= 2
    return result

def find_order(a, m):
    if gcd(a, m) != 1:
        return -1
    phi = m
    temp = m
    factors = []
    for i in range(2, int(m**0.5) + 1):
        if temp % i == 0:
            factors.append(i)
            while temp % i == 0:
                temp //= i
    if temp > 1:
        factors.append(temp)
    for p in factors:
        phi = phi * (1 - (1/p))

    phi = int(phi)
    divisors = []
    for i in range(1, int(phi**0.5) + 1):
        if phi % i == 0:
            divisors.append(i)
            if i != phi // i:
                divisors.append(phi // i)
    divisors.sort()
    for d in divisors:
        if mod_pow(a, d, m) == 1:
            return d
    return phi

