def find_position(step):
    n = 1
    while True:
        # Calculate the number of steps to complete the current square
        steps_in_square = 4 * n ** 2 - 4 * n
        if step <= steps_in_square:
            break
        step -= steps_in_square
        n += 1

    # Calculate the position of the ant within the current square
    top_left = (n, n)
    top_right = (n, 2 * n - 1)
    bottom_left = (2 * n - 1, n)
    bottom_right = (2 * n - 1, 2 * n - 1)

    # Determine which side of the square the ant is on
    if step <= n:
        # Ant is on the top side
        return (n, n + step - 1)
    elif step <= 2 * n:
        # Ant is on the right side
        return (n + step - n, 2 * n - 1)
    elif step <= 3 * n:
        # Ant is on the bottom side
        return (2 * n - 1, 2 * n - 1 - (step - 2 * n))
    else:
        # Ant is on the left side
        return (2 * n - 1 - (step - 3 * n), n)

