            for side in range(1, 101):
                # Check if the square is within the boundaries of the armchair
                if x + side > 100 or y + side > 100:
                    break

                # Check if the square contains any arches
                contains_arch = False
                for arch in arches:
                    if x <= arch[0] < x + side and y <= arch[1] < y + side:
                        contains_arch = True
                        break

