def last_digit_of_power(n, power):
    last_digit = n % 10
    if last_digit == 0:
        return 0
    elif last_digit == 1:
        return 1
    elif last_digit == 5:
        return 5
    elif last_digit == 6:
        return 6
    elif last_digit == 4 or last_digit == 9:
        cycle_length = 2
    else:
        cycle_length = 4
