def is_pseudo_palindrome(n):
    """Check if a number is a pseudo-palindrome."""
    digit_counts = {}
    for digit in str(n):
        if digit in digit_counts:
            digit_counts[digit] += 1
        else:
            digit_counts[digit] = 1

    odd_counts = 0
    for count in digit_counts.values():
        if count % 2 != 0:
            odd_counts += 1
        if odd_counts > 1:
            return False
    return True

def find_largest_pseudo_palindrome(limit):
    """Find the largest pseudo-palindrome less than or equal to the limit."""
    for i in range(limit, 0, -1):
        if is_pseudo_palindrome(i):
            return i
    return None

