def greedy_algorithm(sum_to_cover, banknotes):
    banknotes = sorted(banknotes, reverse=True)
    remaining_sum = sum_to_cover
    used_banknotes = []
    for banknote in banknotes:
        while remaining_sum >= banknote:
            used_banknotes.append(banknote)
            remaining_sum -= banknote
    return remaining_sum == 0, used_banknotes

def can_cover_sum(sum_to_cover, banknotes):
    if sum_to_cover == 0:
        return True
    if not banknotes:
        return False
    for i in range(len(banknotes)):
        if banknotes[i] <= sum_to_cover:
            if can_cover_sum(sum_to_cover - banknotes[i], banknotes[i:]):
                return True
    return False

def find_s_min(banknotes):
    sum_to_cover = 1
    while True:
        greedy_success, _ = greedy_algorithm(sum_to_cover, banknotes)
        if not greedy_success:
            if can_cover_sum(sum_to_cover, banknotes):
                return sum_to_cover
        sum_to_cover += 1

