def count_ways(m, n):
    # Initialize a DP table of size (m+1) x (n+1)
    dp = [[0] * (n + 1) for _ in range(m + 1)]
    dp[0][0] = 1  # Base case

    for i in range(m + 1):
        for j in range(n + 1):
            if i == 0 and j == 0:
                continue
            total = 0
            if i >= 1:
                total += dp[i-1][j]
            if i >= 2:
                total += dp[i-2][j]
            if j >= 1:
                total += dp[i][j-1]
            if j >= 2:
                total += dp[i][j-2]
            dp[i][j] = total

    return dp[m][n]

