def find_largest_non_expressible():
    max_k = 0
    max_limit = 100  # A reasonable upper limit to check
    for K in range(1, max_limit + 1):
        expressible = False
        for a in range(K // 5 + 1):
            for b in range((K - 5 * a) // 7 + 1):
                remaining = K - 5 * a - 7 * b
                if remaining >= 0 and remaining % 10 == 0:
                    expressible = True
                    break
            if expressible:
                break
        if not expressible:
            max_k = K
    return max_k

