def multiplicative_order(a, n):
    if gcd(a, n) != 1:
        return None
    phi = euler_phi(n)
    order = phi
    factors = factorize(phi)
    for p in factors:
        while order % p == 0:
            if pow(a, order // p, n) == 1:
                order = order // p
            else:
                break
    return order

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def euler_phi(n):
    result = n
    p = 2
    while p * p <= n:
        if n % p == 0:
            while n % p == 0:
                n = n // p
            result -= result // p
        p += 1
    if n > 1:
        result -= result // n
    return result

def factorize(n):
    factors = set()
    while n % 2 == 0:
        factors.add(2)
        n = n // 2
    p = 3
    while p * p <= n:
        while n % p == 0:
            factors.add(p)
            n = n // p
        p += 2
    if n > 1:
        factors.add(n)
    return factors

