import itertools

def find_minimum_spanning_trees():
    # Define the edges of the graph
    edges = [
        (1, 2, 2),
        (1, 4, 2),
        (2, 5, 2),
        (2, 3, 1),
        (3, 6, 2),
        (4, 5, 2),
        (5, 6, 1)
    ]

    # Sort the edges by their weights
    edges.sort(key=lambda x: x[2])

    # Initialize the minimum spanning trees
    minimum_spanning_trees = []

    # Generate all possible combinations of edges
    for combination in itertools.combinations(edges, 5):
        # Check if the combination forms a minimum spanning tree
        if is_minimum_spanning_tree(combination):
            minimum_spanning_trees.append(combination)

    return len(minimum_spanning_trees)

def is_minimum_spanning_tree(edges):
    # Check if the edges form a connected graph
    if not is_connected(edges):
        return False

    # Check if the edges form a tree (i.e., they do not contain any cycles)
    if has_cycle(edges):
        return False

    return True

def is_connected(edges):
    # Create an adjacency list representation of the graph
    adjacency_list = {}
    for edge in edges:
        if edge[0] not in adjacency_list:
            adjacency_list[edge[0]] = []
        if edge[1] not in adjacency_list:
            adjacency_list[edge[1]] = []
        adjacency_list[edge[0]].append(edge[1])
        adjacency_list[edge[1]].append(edge[0])

    # Perform a depth-first search to check if the graph is connected
    visited = set()
    stack = [next(iter(adjacency_list))]
    while stack:
        node = stack.pop()
        if node not in visited:
            visited.add(node)
            stack.extend(adjacency_list[node])

    return len(visited) == 6

def has_cycle(edges):
    # Create an adjacency list representation of the graph
    adjacency_list = {}
    for edge in edges:
        if edge[0] not in adjacency_list:
            adjacency_list[edge[0]] = []
        if edge[1] not in adjacency_list:
            adjacency_list[edge[1]] = []
        adjacency_list[edge[0]].append(edge[1])
        adjacency_list[edge[1]].append(edge[0])

    # Perform a depth-first search to check if the graph contains any cycles
    visited = set()
    stack = [next(iter(adjacency_list))]
    while stack:
        node = stack.pop()
        if node not in visited:
            visited.add(node)
            stack.extend(adjacency_list[node])

    return len(visited) != 6 or len(edges) != 5

print(find_minimum_spanning_trees())

