def count_lucky_tickets():
    max_sum = 27
    ways = [0] * (max_sum + 1)

    # Initialize base case
    ways[0] = 1

    # Compute the number of ways to get each sum with up to three digits
    for _ in range(3):
        new_ways = ways[:]
        for i in range(max_sum + 1):
            if ways[i] > 0:
                for j in range(10):
                    if i + j <= max_sum:
                        new_ways[i + j] += ways[i]
        ways = new_ways

    # Calculate the total number of lucky tickets
