def count_lucky_tickets():
    # Initialize a DP table to count the number of ways to achieve each sum with 3 digits
    dp = [[0] * 28 for _ in range(4)]
    dp[0][0] = 1  # Base case: 0 digits sum to 0

    for i in range(1, 4):
        for j in range(28):
            for k in range(10):
                if j - k >= 0:
                    dp[i][j] += dp[i-1][j-k]

    # Calculate the total number of lucky tickets
    total = 0
    for s in range(28):
        total += dp[3][s] * dp[3][s]

    return total

