import requests

def count_empty_directories(url):
    response = requests.get(url)
    lines = response.text.splitlines()
    empty_directories = 0
    for line in lines:
        if line.strip() == '':
            continue
        if '--' not in line:
            # This is a directory name
            has_subdirectories = False
            for next_line in lines[lines.index(line) + 1:]:
                if next_line.strip() == '':
                    break
                if len(next_line) - len(next_line.lstrip()) > len(line) - len(line.lstrip()):
                    has_subdirectories = True
                    break
            if not has_subdirectories:
                empty_directories += 1
    return empty_directories

