def find_max_sum():
    capacities = [14, 3, 16, 8, 2, 5]
    max_sum = 0
    sums_count = {}

    # Generate all possible subsets
    for i in range(1 << len(capacities)):
        subset_sum = sum([capacities[j] for j in range(len(capacities)) if (i & (1 << j))])

        # Count occurrences of each sum
        if subset_sum in sums_count:
            sums_count[subset_sum] += 1
        else:
            sums_count[subset_sum] = 1

    # Find the maximum sum that occurs exactly twice
    for sum_value, count in sums_count.items():
        if count == 2 and sum_value > max_sum:
            max_sum = sum_value

    return max_sum

