    x_turn = True
    while True:
        if x_turn:
            # X's turn, choose the smallest factor that could lead to a win
            for factor in range(2, 10):
                if p * factor >= n:
                    return True
            # If no winning move, choose the smallest factor
            p *= 2
        else:
            # Y's turn, choose the largest factor to avoid giving X a win
            for factor in range(9, 1, -1):
                if p * factor >= n:
                    return False
            # If no losing move, choose the largest factor
            p *= 9
        x_turn = not x_turn

