def find_position(step):
    layer = 1
    while (layer * (layer + 1)) // 2 * 4 < step:
        layer += 1

    steps_in_layer = step - ((layer - 1) * layer // 2 * 4)

    # Calculate the position of the ant in the current layer
    side_length = 2 * layer - 1
    top_left = (layer, layer)
    top_right = (layer, side_length - layer + 1)
    bottom_left = (side_length - layer + 1, layer)
    bottom_right = (side_length - layer + 1, side_length - layer + 1)

    if steps_in_layer <= side_length - 1:
        # Moving down
        return (layer + steps_in_layer, layer)
    elif steps_in_layer <= 2 * (side_length - 1):
        # Moving right
        return (side_length, layer + steps_in_layer - (side_length - 1))
    elif steps_in_layer <= 3 * (side_length - 1):
        # Moving up
        return (side_length - (steps_in_layer - 2 * (side_length - 1)), side_length)
    else:
        # Moving left
        return (layer + 1, side_length - (steps_in_layer - 3 * (side_length - 1)))

