def last_digit_of_power(n, power):
    last_digit = n % 10
    if last_digit == 0:
        return 0
    if last_digit == 1:
        return 1
    if last_digit == 5:
        return 5
    if last_digit == 6:
        return 6
    # For other digits, we need to find the pattern
    if last_digit == 2:
        cycle = [2, 4, 8, 6]
    elif last_digit == 3:
        cycle = [3, 9, 7, 1]
    elif last_digit == 4:
        cycle = [4, 6]
    elif last_digit == 7:
        cycle = [7, 9, 3, 1]
    elif last_digit == 8:
        cycle = [8, 4, 2, 6]
    elif last_digit == 9:
        cycle = [9, 1]
    # Find the position in the cycle
    pos = (power - 1) % len(cycle)
    return cycle[pos]

