import datetime
import itertools

def is_valid_date(year, month, day, hour, minute):
    try:
        datetime.datetime(year, month, day, hour, minute)
        return True
    except ValueError:
        return False

def is_robust_date(year, month, day, hour, minute):
    components = [day, month, hour, minute, year]
    for p in itertools.permutations(components):
        if p[0] != day or p[1] != month or p[2] != hour or p[3] != minute or p[4] != year:
            if is_valid_date(p[4], p[1], p[0], p[2], p[3]):
                return False
    return True

def count_robust_dates():
    count = 0
    for year in range(2021, 2022):
        for month in range(1, 13):
            for day in range(1, 32):
                for hour in range(24):
                    for minute in range(60):
                        if is_valid_date(year, month, day, hour, minute) and is_robust_date(year, month, day, hour, minute):
                            count += 1
    return count

print(count_robust_dates())
