import random

def simulate_day(children, secret_knowers, friendships):
    new_knowers = set()
    for child in secret_knowers:
        friend = random.choice(friendships[child])
        if friend not in secret_knowers:
            new_knowers.add(friend)
    return secret_knowers.union(new_knowers)

def simulate_process(friendships, start_knowers, target, days):
    secret_knowers = set(start_knowers)
    for _ in range(days):
        secret_knowers = simulate_day(None, secret_knowers, friendships)
    return target in secret_knowers

def estimate_probability(friendships, start_knowers, target, days, simulations):
    count = 0
    for _ in range(simulations):
        if simulate_process(friendships, start_knowers, target, days):
            count += 1
    return count / simulations

