def count_divisors(n):
    count = 0
    for i in range(1, int(n**0.5) + 1):
        if n % i == 0:
            if n // i == i:
                count += 1
            else:
                count += 2
    return count

def calculate_secure_connections():
    players = 24
    groups = {}
    for i in range(2, players + 1):
        num_divisors = count_divisors(i)
        if num_divisors not in groups:
            groups[num_divisors] = 0
        groups[num_divisors] += 1

    total_connections = 0
    for group in groups.values():
        total_connections += group * (group - 1) // 2

