def count_empty_directories(filename):
    with open(filename, 'r') as f:
        lines = f.readlines()

    empty_directories = 0
    stack = []
    for line in lines:
        line = line.strip()
        if line.startswith("|-- "):
            stack.append(line[4:])
        elif line.startswith("|   "):
            continue
        elif line.startswith("-- "):
            if not stack:
                empty_directories += 1
            stack.pop()
        else:
            if not stack:
                empty_directories += 1

    return empty_directories

