def count_paths(m, n):
    # Initialize the dp array with zeros
    dp = [[0] * (n + 1) for _ in range(m + 1)]

    # Base case
    dp[0][0] = 1

    # Fill the dp array
    for i in range(m + 1):
        for j in range(n + 1):
            if i > 0:
                dp[i][j] += dp[i-1][j]
            if i > 1:
                dp[i][j] += dp[i-2][j]
            if j > 0:
                dp[i][j] += dp[i][j-1]
            if j > 1:
                dp[i][j] += dp[i][j-2]

    return dp[m][n]

