def count_valid_strings():
    # Initialize the DP table
    # dp[i][j][k] where i is the length, j is the last character, k is the consecutive count
    dp = {}

    # Base case: strings of length 1
    dp[1] = {'a': {1: 1}, 'b': {1: 1}}

    # Fill the DP table for lengths from 2 to 10
    for length in range(2, 11):
        dp[length] = {'a': {}, 'b': {}}
        for last_char in ['a', 'b']:
            for consecutive_count in dp[length - 1][last_char]:
                # If we add the same character, increment the consecutive count
                if consecutive_count < 2:
                    new_consecutive_count = consecutive_count + 1
                    if new_consecutive_count not in dp[length][last_char]:
                        dp[length][last_char][new_consecutive_count] = 0
                    dp[length][last_char][new_consecutive_count] += dp[length - 1][last_char][consecutive_count]
                # If we add the other character, reset the consecutive count
                other_char = 'b' if last_char == 'a' else 'a'
                if 1 not in dp[length][other_char]:
                    dp[length][other_char][1] = 0
                dp[length][other_char][1] += dp[length - 1][last_char][consecutive_count]

    # Sum all valid states for length 10
    total = 0
    for last_char in ['a', 'b']:
        for consecutive_count in dp[10][last_char]:
            total += dp[10][last_char][consecutive_count]

    return total

