def simulate_game(n):
    p = 1
    x_turn = True
    turns = 0
    while p < n:
        if x_turn:
            # X's turn, try to find the smallest multiplier that doesn't let Y win immediately
            for i in range(2, 10):
                new_p = p * i
                if new_p >= n:
                    return True  # X wins
                # If Y can't win on the next turn, choose this multiplier
                y_can_win = False
                for j in range(2, 10):
                    if new_p * j >= n:
                        y_can_win = True
                        break
                if not y_can_win:
                    p = new_p
                    break
            else:
                # If no good move is found, just multiply by 2
                p *= 2
        else:
            # Y's turn, try to find the smallest multiplier that doesn't let X win immediately
            for i in range(2, 10):
                new_p = p * i
                if new_p >= n:
