def ant_position(step):
    # Calculate the side length of the square
    n = 1
    while (n * n) < step:
        n += 2

    # Calculate the position of the ant within the square
    pos = step - ((n - 2) ** 2)
    side = n

    # Determine the position of the ant
    if pos <= side:
        return (side - pos + 1, 1)
    elif pos <= 2 * side:
        return (1, pos - side + 1)
    elif pos <= 3 * side:
        return (pos - 2 * side + 1, side)
    else:
        return (side, 4 * side - pos + 1)

