import math

def find_primes(n):
    primes = []
    sieve = [True] * (n + 1)
    for x in range(2, int(math.sqrt(n)) + 1):
        if sieve[x]: 
            for i in range(x*x, n + 1, x): 
                sieve[i] = False
    for x in range(2, n):
        if sieve[x]: 
            primes.append(x)
    return primes

def count_numbers_with_5_divisors():
    limit = 2000000000
    primes = find_primes(int(math.pow(limit, 0.25)) + 1)
    count = 0
    for p in primes:
        num = p ** 4
        if num <= limit:
            count += 1
    return count

