def min_steps(start, end):
    dp = [float('inf')] * (end + 1)
    dp[start] = 0

    for i in range(start, end + 1):
        if dp[i] != float('inf'):
            for step in [1, 2, 3]:
                if i + step <= end:
                    dp[i + step] = min(dp[i + step], dp[i] + 1)

    return dp[end]

