def max_wall_area(posters, wall_length):
    n = len(posters)
    dp = [[0] * (wall_length + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(wall_length + 1):
            if posters[i-1] <= j:
                dp[i][j] = max(dp[i-1][j], dp[i-1][j-posters[i-1]] + posters[i-1])
            else:
                dp[i][j] = dp[i-1][j]

    return dp[n][wall_length]

