def greedy_algorithm(sum, banknotes):
    banknotes = sorted(banknotes, reverse=True)
    remaining_sum = sum
    for note in banknotes:
        while remaining_sum >= note:
            remaining_sum -= note
    return remaining_sum == 0

def find_s_min(banknotes):
    s = 1
    while True:
        if not greedy_algorithm(s, banknotes):
            return s
        s += 1

